import React, { useState, useEffect } from 'react';

function App() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState([]);
  const [searchValue, setSearchValue] = useState('')

  const fetchData = () => 
  fetch("/positions.json?description=python")
  .then(res => res.json())
  .then(
    (result) => {
      setIsLoaded(true);
      setData(result);
    },

    (error) => {
      setIsLoaded(true);
      setError(error);
    }
  )

  // useEffect(() => {
  //   fetchData().then(
  //           (result) => {
  //             setIsLoaded(true);
  //             setData(result);
  //           },

  //           (error) => {
  //             setIsLoaded(true);
  //             setError(error);
  //           }
  //         )
  // }, []);

  const onSearch = (e) => {
        e.preventDefault();
        fetchData(searchValue)
        setSearchValue('')
    }

  const onSearchInputChange = (e) => {
      setSearchValue(e.target.value.trim())
  }

   if (error) {
    return <div>Error: {error.message}</div>;
  } 

  if (!isLoaded) {
    return <div>Loading...</div>;
  }

  return (
     <div className="container">
        <ul>
          {data.map(item => (
            <li key={item.id}>
              {item.company} {item.type}
            </li>
          ))}
        </ul>
        <form className="search-form py-5">
            <div className="form-row">
                <div className="form-group col-md-10 col-sm-10">
                    <input type="text" className="form-control" value={searchValue} onChange={onSearchInputChange} placeholder="search"/>
                </div>
                <div className="form-group col-md-2 col-sm-2"> 
                    <button onClick={onSearch} type="button" label="Search" className="btn btn-primary">Search</button>
                </div>
            </div>              
        </form>
        </div>
      );
}

export default App;
