import * as React from 'react';

const LoadMore = (props) => {
    const { products, isLoading, onClick } = props;
    const showBtn = () => {
        return (products.length > 4 && !isLoading ) && <button className="btn btn-primary btn-block mb-3 mx-3" onClick={onClick}>Show more!</button>
    }
    return (
        showBtn()
    );
}

export {LoadMore};
