import React from 'react';

const ProductList = (props) => {
    const { products, isLoading, errorMessage, total, pageSize } = props;

    if (isLoading && !errorMessage) {
        return <div className="col-12 text-white text-center">Loading...</div>;
    }

    return ( 
        <div className="col-12 text-white">
            {(total === 0 && !isLoading) && <div className="text-center">Not found product..</div>}
            {errorMessage && <div>{errorMessage}</div>}
            <ul className="search-result">
                {!isLoading && products.slice(0,pageSize).map((product, i) => { // show only 4 results
                    return (<li key={i} id={product.id}><strong>{product.brand}</strong> - {product.name}</li>)
                    })
                }
            </ul> 
        </div>
    )
}

export default ProductList;
